package algorithm;

import java.util.Scanner;

public class Main10952 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan = new Scanner(System.in);
		while(scan.hasNext()) {
			int A = scan.nextInt();
			int B = scan.nextInt();
			System.out.println(A + B);
		}
	}

}
